MOLECULE = ''
GOAL = 'e'


def parse_input(input_string):
    results = []
    for row in input_string.strip().split('\n'):
        m1, m2 = row.split(' => ')
        results.append((m2, m1))
    results.sort(key=lambda x: -len(x[0]))
    return results


def search(start, goal, replacements):
    count = 0
    while True:
        t = start
        start = replace(start, replacements)
        count += 1
        if start == goal:
            return count, goal
        elif start == t:
            return None, start


def replace(molecule, replacements):
    for old, new in replacements:
        if old in molecule:
            return molecule.replace(old, new, 1)
    return molecule


def main():
    replacements = parse_input(INPUT)
    for _ in range(50):
        shuffle(replacements)
        result = search(MOLECULE, GOAL, replacements)
        print(result)


if __name__ == "__main__":
    main()
