def part1():
    up = data.count('(')
    down = data.count(')')
    return up - down


def part2():
    floor = 0
    for i, ch in enumerate(data):
        if ch == '(':
            floor += 1
        else:
            floor -= 1

        if floor == -1:
            return i + 1


def main():
    '''Main entry point.'''
    p1 = part1()
    print(p1)

    p2 = part2()
    print(p2)


